import React, {Component} from "react";
import {Button, Col, Form, FormGroup, Input, Label, Modal, ModalBody, ModalHeader, Row} from "reactstrap";
import {Control} from "react-redux-form";


class CommentForm extends Component{
    constructor(props) {
        super(props);

        this.state = {
            isModalOpen: false
        }
        this.toggleModal = this.toggleModal.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    toggleModal(){
        this.setState({
            isModalOpen: !this.state.isModalOpen
        })
    }
    handleSubmit(event) {
        this.toggleModal();
        alert("Username: " + this.username.value);
        event.preventDefault();

    }


    render() {
        return (

            <div className={"CommentForm"}>

                <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                    <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                    <ModalBody>
                        <Form onSubmit={this.handleLogin}>
                            <FormGroup>
                                <Label htmlFor={"rating"}>
                                    Rating
                                </Label>
                                <select name="contactType" className="form-control" id={"rating"}>
                                    <option>1</option>
                                    <option>2</option>
                                    <option>3</option>
                                    <option>4</option>
                                    <option>5</option>
                                </select>
                            </FormGroup>
                            <FormGroup>
                                <Label htmlFor={"name"}>
                                    Your Name
                                </Label>
                                <Input type={"text"} id={"name"} name={"name"}
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label htmlFor={"comment"}>
                                    Comment
                                </Label>
                                <Input type={"textarea"} id={"textarea"} name={"textarea"}
                                       rows={"6"}
                                />
                            </FormGroup>
                            <Button type="submit" value="submit" color="primary">Submit</Button>
                        </Form>
                    </ModalBody>
                </Modal>

                <Button outline onClick={this.toggleModal}><span className="fa fa-pencil fa-lg"/> Submit Comment</Button>
            </div>
        );
    }

}

export default CommentForm;
